%angular
<!-- Avoid constantly editing JS and list the Angular vars you want exposed in an HTML attribute: -->
<div id="dummy" vars="centroid,locs"></div>
<script type="text/javascript">
  //Given an element in the note & list of values to fetch from Spark
  //window.angularVars.myVal will be current value of backend Spark val of same name
  function hoist(element){
    var varNames = element.attr('vars').split(',');
    window.angularVars = {};
    var scope = angular.element(element.parent('.ng-scope')).scope().compiledScope;
    $.each(varNames, function(i, v){
      window[v+'-watcher'] = scope.$watch(v, function(newVal, oldVal){
        window.angularVars[v] = newVal;
      });
    });
  }
  hoist($('#dummy'));
</script>

%angular
<div id="map" style="height:500px;width:100%;"></div>
<script type="text/javascript">
function circle(pos, color, map){
    var circle = new google.maps.Circle({
        strokeColor: color,
        strokeOpacity: 0.8,
        strokeWeight: 1,
        fillColor: color,
        fillOpacity: 0.3,
        map: map,
        center: pos,
        radius: 500   //default: 6.5
    });
}

function initMap() {
    var centroid = window.angularVars.centroid;
  //  var colors = ["blue", "red","green","OrangeRed","purple", "orange","BlueViolet","Maroon", "HotPink", "LightCoral"]
    var colors =['#e6194b', '#3cb44b', '#ffe119', '#4363d8', '#f58231', '#911eb4', '#46f0f0', '#f032e6', '#bcf60c', '#fabebe', '#008080', '#e6beff', '#9a6324', '#fffac8', '#800000', '#aaffc3', '#808000', '#ffd8b1', '#000075', '#808080', '#ffffff']
    var locs = window.angularVars.locs;
    var NYC = {lat: 40.76664545866995, lng: -73.971943532955};
    var map = new google.maps.Map(document.getElementById('map'), {zoom: 11, center: NYC });
    //  Draw circles for each Centroid
    $.each(centroid, function(i, v){
         var pos = {lat: parseFloat(v.values[0]), lng: parseFloat(v.values[1]) };
          marker = new google.maps.Marker({
                            position: pos,
                            label: String(i),
                            map: map
                        });
    });
    $.each(locs, function(i, v){
         var pos = {lat: parseFloat(v.values[0]), lng: parseFloat(v.values[1]) };
         var color =colors[ v.values[2]] ;
         circle(pos, color, map);
    });

}

//Only load GMaps once
if (typeof google === 'object' && typeof google.maps === 'object') initMap();
else {
    var script = document.createElement("script");
    script.type = "text/javascript";
    //Replace with your API key
    var apiKey = 'AIzaSyBbKXTPdUuKimkq5ONrChpNuxnI5SMhMUI';
    script.src = "https://maps.googleapis.com/maps/api/js?key="+apiKey+"&libraries=visualization&callback=initMap";
    document.body.appendChild(script);
}
</script>
